sqlMap.insert:
DECLARE /* FAME0050.SAVE00 최초이월잔액(계정)저장 */
sSLIP_YSNO VARCHAR2(2); sACCT_YSNO VARCHAR2(2);
BEGIN BEGIN /* 1. 기표가능여부 체크 */ SELECT SLIP_YSNO, ACCT_YSNO INTO sSLIP_YSNO, sACCT_YSNO FROM TA_ACCTXM WHERE ACCT_INTL = #ACCT_INTL# /* 계정과목코드 */ ;
IF sSLIP_YSNO <> '1' OR sACCT_YSNO <> '1' THEN RAISE_APPLICATION_ERROR(-20000, '전표등록 불가능한 계정과목코드 입니다. [' || #ACCT_INTL# || ']'); END IF;
EXCEPTION WHEN NO_DATA_FOUND THEN RETURN; RAISE_APPLICATION_ERROR(-20000, '존재하지 않는 계정과목코드 입니다. [' || #ACCT_INTL# || ']'); END;
DELETE TA_MAMNTD WHERE ACCT_UNIT = #ACCT_UNIT# AND ACCT_GUBN = #ACCT_GUBN# AND ACCT_DATE = #ACCT_DATE# AND ACCT_INTL = #ACCT_INTL# AND CURR_GUBN = #CURR_GUBN# AND COND_CODE = #COND_CODE# AND COND_VACD = #COND_VACD# ;
INSERT INTO TA_MAMNTD ( ACCT_UNIT /* 회계단위 */ ,ACCT_GUBN /* 회계구분_사업장 */ ,ACCT_DATE /* 회계일자 */ ,ACCT_INTL /* 계정과목코드 */ ,CURR_GUBN /* 통화구분 */ ,COND_CODE /* 관리항목코드 */ ,COND_VACD /* 관리항목값 */ ,COND_VANM /* 관리항목명 */ ,SLIP_CHAX /* 전표차변 */ ,SLIP_DAEX /* 전표대변 */ ,FORI_CHAX /* 외화차변 */ ,FORI_DAEX /* 외화대변 */ ,INST_USID /* 입력작업자 */ ,INST_DATE /* 입력일시 */ ,UPDT_USID /* 수정작업자 */ ,UPDT_DATE /* 수정일시 */ ) VALUES ( #ACCT_UNIT# /* 회계단위 */ ,#ACCT_GUBN# /* 회계구분_사업장 */ ,#ACCT_DATE# /* 회계일자 */ ,#ACCT_INTL# /* 계정과목코드 */ ,#CURR_GUBN# /* 통화구분 */ ,#COND_CODE# /* 관리항목코드 */ ,#COND_VACD# /* 관리항목값 */ ,#COND_VANM# /* 관리항목명 */ ,NVL(#SLIP_CHAX#, 0) /* 전표차변 */ ,NVL(#SLIP_DAEX#, 0) /* 전표대변 */ ,NVL(#FORI_CHAX#, 0) /* 외화차변 */ ,NVL(#FORI_DAEX#, 0) /* 외화대변 */ ,#UPDT_USID# /* 입력작업자 */ ,SYSDATE /* 입력일시 */ ,#UPDT_USID# /* 수정작업자 */ ,SYSDATE /* 수정일시 */ ) ;
END;
