sqlMap.update:
BEGIN UPDATE FS_CLSFXM /*FSMA0020.UPDATE00.품목분류 관리 수정 처리*//*[품목분류]*/ SET CLSF_NAME = #CLSF_NAME# /*품목분류명*/ ,CLSF_LEVE = #CLSF_LEVE# /*폼목분류레벨*/ ,ASCL_CODE = CASE WHEN #CLSF_LEVE# = '1' THEN #ASCL_CODE# ELSE TO_CHAR((SELECT ASCL_CODE FROM FS_CLSFXM WHERE CLSF_LEVE = '1' AND SUBSTR(CLSF_CODE, 1, 2) = SUBSTR(#CLSF_CODE#, 1, 2))) END /*자산분류코드*/ ,SORT_ORDR = #SORT_ORDR# /*정렬순서*/ ,USEX_YSNO = #USEX_YSNO# /*사용여부*/ ,BEFO_CLSF = #BEFO_CLSF# /*이전분류코드*/ ,UPDT_DATE = SYSDATE /*수정일시*/ ,UPDT_USID = #UPDT_USID# /*수정자*/ ,UPXX_CLSF = #UPXX_CLSF# /*상위분류코드*/ WHERE CLSF_CODE = #CLSF_CODE# /*품목분류코드*/ ;
/*1레벨 자산분류코드가 변경 되었을 경우 하위레벨의 자산분류코드를 모두 1레벨로 변경*/ IF #CLSF_LEVE# = '1' THEN
UPDATE FS_CLSFXM /*FSMA0020.UPDATE00.1레벨_품목분류 관리 수정 처리. */ SET ASCL_CODE = #ASCL_CODE# /*자산분류코드*/ ,UPDT_DATE = SYSDATE /*수정일시*/ ,UPDT_USID = #UPDT_USID# /*수정자*/ WHERE CLSF_LEVE <> '1' AND SUBSTR(CLSF_CODE, 1, 2) = SUBSTR(#CLSF_CODE#, 1, 2); /*품목분류코드*/
END IF;
END;

sqlMap.delete:

		DECLARE
			iCNT                    DECIMAL(15,0);   /*확인용*/

		BEGIN


			 SELECT COUNT(1)
              INTO iCNT
              FROM FS_ASSTXM B
             WHERE B.CLSF_CODE = TO_NCHAR(#CLSF_CODE#);

             IF (iCNT > 0) THEN
	           RAISE_APPLICATION_ERROR(-20000, '해당 분류를 사용하는 고정자산데이터가 존재합니다.\n\n 삭제 불가 합니다!');
	        END IF;


			DELETE FROM FS_CLSFXM A /*FSMA0020.DELETE00.품목분류 관리 삭제 처리*//*[품목분류]*/
	         WHERE A.CLSF_CODE = TO_NCHAR(#CLSF_CODE#)  /*품목분류코드*/
	         ;

	    END;
