UPDATE HR_MASTXD /* [인사기타정보] */ SET POST_NUMB = #POST_NUMB# /* 우편번호 */ , DTL1_ADDR = #DTL1_ADDR# /* 상세1주소 */ , DTL2_ADDR = #DTL2_ADDR# /* 상세2주소 */ , RERN_PONB = #RERN_PONB# /* 주민등록우편번호 */ , RGD1_ADDR = #RGD1_ADDR# /* 주민등록상세1주소 */ , RGD2_ADDR = #RGD2_ADDR# /* 주민등록상세2주소 */ , LEGL_PONB = #LEGL_PONB# /* 본적우편번호 */ , LED1_ADDR = #LED1_ADDR# /* 본적상세1주소 */ , LED2_ADDR = #LED2_ADDR# /* 본적상세2주소 */ , COMP_TENR = #COMP_TENR# /* 회사전화번호 */ , HNPN_TENR = #HNPN_TENR# /* 휴대전화번호 */ , OHOM_TENR = #OHOM_TENR# /* 자택전화번호 */ , ELTR_MLAD = #ELTR_MLAD# /* 전자메일주소 */ , EMPL_HEGT = #EMPL_HEGT# /* 사원신장 */ , BODY_WEGT = #BODY_WEGT# /* 몸무게 */ , LEFT_SGHT = #LEFT_SGHT# /* 좌측시력 */ , RIGH_SGHT = #RIGH_SGHT# /* 우측시력 */ , BLTP_CODE = #BLTP_CODE# /* 혈액형코드 */ , REAL_BIRT = #REAL_BIRT# /* 실제생년월일 */ , SLCA_CODE = #SLCA_CODE# /* 양력음력구분코드 */ , MARG_YSNO = #MARG_YSNO# /* 결혼여부 */ , WEAN_DATE = #WEAN_DATE# /* 결혼기념일 */ , RLGN_CODE = #RLGN_CODE# /* 종교코드 */ , EMPL_HOBY = #EMPL_HOBY# /* 사원취미 */ , EMPL_SPEC = #EMPL_SPEC# /* 사원특기 */ , HOFM_NAME = #HOFM_NAME# /* 호주성명 */ , HOFM_RELA = #HOFM_RELA# /* 호주관계명 */ , HALT_CODE = #HALT_CODE# /* 장애코드 */ , DEDY_CODE = #DEDY_CODE# /* 장애등급코드 */ , DEGC_CODE = #DEGC_CODE# /* 장애유형코드 */ , HAAD_DATE = #HAAD_DATE# /* 장애인정일 */ , HALT_PROW = #HALT_PROW# /* 장애사유 */ , SVIM_YSNO = #SVIM_YSNO# /* 중증장애여부 */ , REDN_CODE = #REDN_CODE# /* 보훈구분코드 */ , RCMP_NUMB = #RCMP_NUMB# /* 보훈번호 */ , RCMP_DATE = #RCMP_DATE# /* 보훈일자 */ , MAMR_CODE = #MAMR_CODE# /* 유공자관계코드 */ , MNOM_NAME = #MNOM_NAME# /* 유공자성명 */ , HDPP_NAME = #HDPP_NAME# /* 해당지청명 */ , MLGN_CODE = #MLGN_CODE# /* 병역구분코드 */ , SOLR_NUMB = #SOLR_NUMB# /* 군인번호 */ , KMIL_CODE = #KMIL_CODE# /* 군별코드 */ , BOTS_CODE = #BOTS_CODE# /* 병과코드 */ , DICL_CODE = #DICL_CODE# /* 제대계급코드 */ , CLSS_CODE = #CLSS_CODE# /* 역종코드 */ , ENLS_DATE = #ENLS_DATE# /* 입대일 */ , LVAM_DATE = #LVAM_DATE# /* 제대일 */ , HPJ1_CNTT = #HPJ1_CNTT# /* 희망업무1내용 */ , HPJ2_CNTT = #HPJ2_CNTT# /* 희망업무2내용 */ , HOP1_ARNM = #HOP1_ARNM# /* 희망1지역명 */ , HOP2_ARNM = #HOP2_ARNM# /* 희망2지역명 */ , RCPS_NAME = #RCPS_NAME# /* 추천자성명 */ , RCPS_TLNM = #RCPS_TLNM# /* 추천자직위명 */ , RCPS_RELA = #RCPS_RELA# /* 추천자관계명 */ , RCPC_NAME = #RCPC_NAME# /* 추천자회사명 */ , CIRP_EMNR = #CIRP_EMNR# /* 사내추천자사원번호 */ , HUSF_CODE = #HUSF_CODE# /* 가옥형태코드 */ , HUSO_CODE = #HUSO_CODE# /* 가옥소유형태코드 */ , UPDT_USID = #UPDT_USID# /* 수정작업자 */ , UPDT_DATE = SYSDATE /* 수정일시 */ , LVTY_CODE = #LVTY_CODE# /* 제대구분코드 */ , ENPT_NUMB = #ENPT_NUMB# /* 영문우편번호 */ , END1_ADDR = #END1_ADDR# /* 영문상세1주소 */ , END2_ADDR = #END2_ADDR# /* 영문상세2주소 */ WHERE EMPL_NUMB = #EMPL_NUMB# /* 사원번호 */ ; UPDATE PY_MASTPM SET RECP_DATE = #RECP_DATE# /* 퇴직기산일 */ ,UPDT_USID = #UPDT_USID# /* 수정작업자 */ ,UPDT_DATE = SYSDATE /* 수정일시 */ WHERE EMPL_NUMB = #EMPL_NUMB# /* 사원번호 */ ;
/* 차기승급일 발령에 반영 */ UPDATE HR_APPOXD A SET NETP_DATE = (SELECT NETP_DATE FROM HR_MASTXM B WHERE A.EMPL_NUMB = B.EMPL_NUMB) WHERE A.EMPL_NUMB = #EMPL_NUMB# AND A.APNT_DATE = (SELECT MAX(APNT_DATE) FROM HR_APPOXD WHERE EMPL_NUMB = A.EMPL_NUMB) AND A.APNT_SEQN = (SELECT MAX(APNT_SEQN) FROM HR_APPOXD WHERE EMPL_NUMB = A.EMPL_NUMB AND APNT_DATE = A.APNT_DATE);
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000,SQLERRM); END;
