sqlMap.insert:
BEGIN /* HRML0030P01.SAVE00.연봉계약서확인 저장 */ INSERT INTO PY_REGSAL ( EMPL_NUMB /*사원번호*/ ,STDS_DATE /*기준일*/ ,SRAL_NUMB /*일련번호*/ ,BSLY_AMOT /*기본급금액*/ ,BSL1_AMOT /*시간외수당*/ ,BSL3_AMOT /*수습1*/ ,BSL4_AMOT /*수습2*/ ,BSL5_AMOT /*수습3*/ ,REMK_100X /*비고100*/ ,INST_USID /*입력작업자*/ ,INST_DATE /*입력일시*/ ,UPDT_USID /*수정작업자*/ ,UPDT_DATE /*수정일시*/ ) VALUES ( #EMPL_NUMB# /*사원번호*/ ,#STDS_DATE# /*기준일*/ , (SELECT NVL(MAX(SRAL_NUMB),0)+1 FROM PY_REGSAL WHERE EMPL_NUMB = #EMPL_NUMB# AND STDS_DATE = #STDS_DATE#) /*일련번호*/ ,#BSLY_AMOT# /*기본급금액*/ ,#OVER_AMOT# /*시간외수당*/ ,#PBN1_AMOT# /*수습1*/ ,#PBN2_AMOT# /*수습2*/ ,#PBN3_AMOT# /*수습3*/ ,#REMK_100X# /*비고100*/ ,#INST_USID# /*입력작업자*/ ,SYSDATE /*입력일시*/ ,#UPDP_USID# /*수정작업자*/ ,SYSDATE /*수정일시*/ ) ;
UPDATE HR_CNTRXA SET CNCF_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD') , CNCF_YSNO = '1' , UPDT_USID = #UPDP_USID# , UPDT_DATE = SYSDATE WHERE EMPL_NUMB = #EMPL_NUMB# AND CNTR_YMTH = #CNTR_YMTH# ;
END;
