sqlMap.insert:
DECLARE /*MACA0090.SAVE00. 매체 위수탁 전표처리*/ CURSOR CUR_MD_SATXXH ( P_TAXY_MONT IN MD_SATXXH.TAXY_MONT%TYPE /*계산서년월*/ ,P_TAXX_NUMB IN MD_SATXXH.TAXX_NUMB%TYPE /*계산서번호*/ ) IS SELECT A.MEDX_CODE ,A.SACH_NUMB ,'100' AS ACCT_GUBN ,B.DEPT_CODE ,A.MEMO_FILD ,B.MATT_NAME ,NVL(A.TAXX_AMTX,0) AS TAXX_AMTX ,NVL(A.TAXX_VATX,0) AS TAXX_VATX ,NVL(A.SUMM_AMTX,0) AS SUMM_AMTX ,CASE WHEN NVL(C.CUTY_CODE,' ') = 'TS100010' THEN '1112310' ELSE '1112320' END AS DEBT_CODE /* 차변계정 */ ,CASE WHEN NVL(C.CUTY_CODE,' ') = 'TS100010' THEN '3151100' ELSE '3151200' END AS ACCT_CODE /* 대변계정 */ FROM MD_SATXXH A LEFT JOIN MD_TRANXD B ON A.TAXY_MONT = B.YEAR_MNTH AND A.TAXX_NUMB = B.TAXX_NUMB AND SF_GET_TAXTRNUM(A.TAXY_MONT,A.TAXX_NUMB) = B.TSEQ_FILD LEFT JOIN TA_CUSTXM C ON B.MEDX_CODE = C.CUST_CODE WHERE 1=1 AND A.BUSI_FLAG= 'B' AND A.TAXY_MONT = P_TAXY_MONT AND A.TAXX_NUMB = P_TAXX_NUMB ;
rMD_SATXXH CUR_MD_SATXXH%ROWTYPE; /*[세금계산서 정보]*/
sSLIP_DATE TA_SLIPXM.SLIP_DATE%TYPE; /*발의일자*/ sEMPL_NUMB TA_SLIPXM.EMPL_NUMB%TYPE; /*입력자*/ sDEPT_CODE TA_SLIPXM.DEPT_CODE%TYPE; /*입력부서*/ sACCT_GUBN TA_SLIPXM.ACCT_GUBN%TYPE; /*사업장*/ sTITL_NAME TA_SLIPXM.TITL_NAME%TYPE; /*대표적요*/ nSLIP_LINE TA_SLIPNT.SLIP_LINE%TYPE; /*전표순번*/
nCURS_SEQN TA_SLIPNT.SLIP_LINE%TYPE; /*커서SEQ*/
sSLIP_NUMB TA_SLIPXM.SLIP_NUMB%TYPE; /*전표번호*/
BEGIN
/*====================================================================================== STEP 1. 전표 생성 여부 체크 ======================================================================================*/ BEGIN OPEN CUR_MD_SATXXH( #TAXY_MONT# /*계산서년월*/ ,#TAXX_NUMB# /*계산서번호*/ ); FETCH CUR_MD_SATXXH INTO rMD_SATXXH; CLOSE CUR_MD_SATXXH;
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, '자산등록 전표 생성 여부 체크 중 오류가 발생하였습니다.'||chr(13)||chr(10)||SQLERRM); END;
IF rMD_SATXXH.SACH_NUMB IS NOT NULL THEN RAISE_APPLICATION_ERROR(-20000, '이미 생성 된 전표(' || rMD_SATXXH.SACH_NUMB || ')가 존재 합니다.'); END IF;
/*====================================================================================== STEP 2. 전표 생성 ======================================================================================*/
/* 발의일자 : 자산등록일*/ sSLIP_DATE := #SLIP_DATE#;
/* 사용자 정보 조회*/ /* BEGIN SELECT U.EMPL_NUMB, U.DEPT_CODE, D.ACCT_GUBN INTO sEMPL_NUMB, sDEPT_CODE, sACCT_GUBN FROM HR_MASTXM U LEFT JOIN VI_DEPTXM D ON D.DEPT_CODE = U.DEPT_CODE WHERE USER_IDXX = UPDT_USID;
EXCEPTION WHEN NO_DATA_FOUND THEN RAISE_APPLICATION_ERROR(-20000, '입력자에 대한 정보를 찾을 수 없습니다.'||chr(13)||chr(10)||SQLERRM); END; */
/* 대표적요 구성 */ sTITL_NAME := '[매체대행] ' || rMD_SATXXH.MATT_NAME;
/* PARAM ----------------------------------------------------- 2-0. 매체 위수탁 전표 > HEADER 추가 --------------------------------------------------------------*/
BEGIN
PKG_FAMIFSLIP.SP_MASTER_SAVE(#SYST_LNCD#, /* ●시스템언어코드*/ #UPDT_USID#, /* ●수정작업자*/ '10', /* ●회계단위 */ #ACCT_GUBN#, /* ●회계구분_사업장 */ sSLIP_DATE, /* ●전표일자(회계일자) */ #SLIP_GUBN#, /* ●전표유형구분 */ sTITL_NAME, /* ●제목 */ '2015008', /* ●사원번호 */ rMD_SATXXH.DEPT_CODE, /* ●부서코드 */ #SOUS_LNKY#, /* ●출처연결키 */ NULL, NULL, NULL, NULL, NULL, sSLIP_NUMB); /* ●전표번호(생성된 전표번호 반환) */
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, 'HEADER 추가 에러'||chr(13)||chr(10)||SQLERRM); END;
/*-------------------------------------------------------------- 2-1.매체 위수탁 전표 > LINE 추가[차>대>차변 순서대로 SLIP_LINE저장요망] --------------------------------------------------------------*/ nSLIP_LINE := 0;
/*-------------------------------------------------------------- 2-1-1. 매체 위수탁 전표 > LINE 추가 > (차변) --------------------------------------------------------------*/ IF rMD_SATXXH.TAXX_AMTX > 0 THEN /*nSLIP_LINE := SLIP_LINE;*/ /*JAVA 에서 받아옴 nSLIP_LINE + 1;*/ nSLIP_LINE := nSLIP_LINE + 1;
BEGIN
PKG_FAMIFSLIP.SP_LINE_SAVE( #SYST_LNCD#, #UPDT_USID#, sSLIP_NUMB, lpad(nSLIP_LINE , '4', '0'), '10', #ACCT_GUBN#, rMD_SATXXH.DEBT_CODE, rMD_SATXXH.DEPT_CODE, '', 'D', 'KRW', 1, rMD_SATXXH.TAXX_AMTX, rMD_SATXXH.TAXX_AMTX, '', '', '', '', '', '', '', '', '', sTITL_NAME, '', '', '', '', '', '', lpad(nSLIP_LINE , '4', '0'), '', '', '', '', '');
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, 'LINE 추가 에러 차변'||chr(13)||chr(10)||SQLERRM); END;
END IF;
/*============================================================= 2-1-2. 매체 위수탁 전표 > LINE 추가 > (대변) ===============================================================*/ IF rMD_SATXXH.SUMM_AMTX > 0 THEN nSLIP_LINE := nSLIP_LINE + 1;
BEGIN
PKG_FAMIFSLIP.SP_LINE_SAVE( #SYST_LNCD#, #UPDT_USID#, sSLIP_NUMB, lpad(nSLIP_LINE , '4', '0'), #ACCT_UNIT#, #ACCT_GUBN#, rMD_SATXXH.ACCT_CODE, rMD_SATXXH.DEPT_CODE, '', 'C', 'KRW', 1, rMD_SATXXH.SUMM_AMTX, rMD_SATXXH.SUMM_AMTX, '', '', '', '', '', '', '', '', '', sTITL_NAME, '', /*cic 임시 증빙종류인데 부가세 라인 생성시 필요함 */ '', '', '', '', '', lpad(nSLIP_LINE , '4', '0'), '', '', '', '', '');
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, 'LINE 추가 에러 대변 미지급금'||chr(13)||chr(10)||SQLERRM); END;
/*============================================================= 2-1-2. 전표라인 관리항목 세트 저장 ===============================================================*/
BEGIN
PKG_FAMIFSLIP.SP_LINERF_SAVE( #SYST_LNCD# /* ●시스템언어코드*/ ,#UPDT_USID# /* ●수정작업자*/ ,sSLIP_NUMB /* ●전표번호 */ ,lpad(nSLIP_LINE , '4', '0') /* ●전표라인번호 */ ,'0002' /* ◎관리항목코드1 */ ,rMD_SATXXH.MEDX_CODE /* ◎관리항목값1 */ ,NULL /* ◎관리항목코드2 */ ,NULL /* ◎관리항목값2 */ ,NULL /* ◎관리항목코드3 */ ,NULL /* ◎관리항목값3 */ ,NULL /* ◎관리항목코드4 */ ,NULL /* ◎관리항목값4 */ ,NULL /* ◎관리항목코드5 */ ,NULL /* ◎관리항목값5 */ ,NULL /* ◎관리항목코드6 */ ,NULL /* ◎관리항목값6 */ ,NULL /* ◎관리항목코드7 */ ,NULL /* ◎관리항목값7 */ ,NULL /* ◎관리항목코드8 */ ,NULL /* ◎관리항목값8 */ ,NULL /* ◎관리항목코드9 */ ,NULL /* ◎관리항목값9 */ );
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, '전표라인 관리항목 세트 저장'||chr(13)||chr(10)||SQLERRM); END;
END IF;
/*============================================================= 2-1-3. 자산등록 전표 > LINE 추가 > 부가가치세매입세액(차변) ===============================================================*/ IF rMD_SATXXH.TAXX_VATX > 0 THEN nSLIP_LINE := nSLIP_LINE + 1; sTITL_NAME := rMD_SATXXH.MATT_NAME || 'VAT';
BEGIN
PKG_FAMIFSLIP.SP_LINE_SAVE( #SYST_LNCD#, #UPDT_USID#, sSLIP_NUMB, lpad(nSLIP_LINE , '4', '0'), #ACCT_UNIT#, #ACCT_GUBN#, '3221100', rMD_SATXXH.DEPT_CODE, '', 'D', 'KRW', 1, rMD_SATXXH.TAXX_VATX, rMD_SATXXH.TAXX_VATX, '', '', '', '', '', '', '', '', '', sTITL_NAME, 'FA180010', '', '', '', '', '', lpad(nSLIP_LINE , '4', '0'), '', '', '', '', '');
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, 'LINE 추가 에러 VAT'||chr(13)||chr(10)||SQLERRM); END;
END IF;
/*============================================================= 2-1-4. 부가세 ===============================================================*/ IF rMD_SATXXH.TAXX_VATX > 0 THEN
BEGIN
PKG_FAMIFSLIP.SP_LINEVAT_SAVE( #SYST_LNCD#, /* ●시스템언어코드*/ #UPDT_USID#, /* ●수정작업자*/ sSLIP_NUMB, /* ●전표번호 */ lpad(nSLIP_LINE , '4', '0'), /* ●전표라인번호 */ '', /* ◎카드번호 */ '', /* ◎카드승인번호 */ sSLIP_DATE, /* ●전송일(계산서발행일/카드승인일) */ 'FA200020', /* ●매입매출구분[VATX_GUBN] */ 'FA210010', /* ●부가세종류[VATX_TYPE] */ '', /* ◎부가세유형[PURC_GUBN] */ '', /* ◎불공제구분[BULG_GUBN] */ 0, /* ◎전자발행여부(1.Yes,0.No) */ rMD_SATXXH.MEDX_CODE, /* ●거래처코드 */ rMD_SATXXH.MATT_NAME, /* ●품목명 */ rMD_SATXXH.TAXX_AMTX, /* ●공급가액 */ rMD_SATXXH.TAXX_VATX, /* ●부가세액 */ rMD_SATXXH.TAXX_AMTX + rMD_SATXXH.TAXX_VATX, /* ●합계금액 */ rMD_SATXXH.MEMO_FILD, /* ●적요 */ 0, /* ◎타사이트발행여부(1.Yes,0.No) */ '', /* ◎미사용여분인수1 */ '' /* ◎미사용여분인수2 */ );
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, '부가세 저장 에러'||chr(13)||chr(10)||SQLERRM); END;
END IF;
/*============================================================= 2-1-6. FINISH ===============================================================*/ IF rMD_SATXXH.TAXX_AMTX > 0 THEN
BEGIN
PKG_FAMIFSLIP.SP_FINISH( #SYST_LNCD# /* ●시스템언어코드*/ ,#UPDT_USID# /* ●수정작업자*/ ,sSLIP_NUMB /* ●전표번호 */ );
EXCEPTION WHEN OTHERS THEN RAISE_APPLICATION_ERROR(-20000, 'FINISH 에러'||chr(13)||chr(10)||SQLERRM); END;
END IF;
/*====================================================================================== STEP 3. 위수탁 전표번호 업데이트 및 전표 테이블에 저장 ======================================================================================*/
UPDATE MD_SATXXH SET SACH_NUMB = sSLIP_NUMB WHERE TAXY_MONT = #TAXY_MONT# AND TAXX_NUMB = #TAXX_NUMB# ;
EXCEPTION WHEN OTHERS THEN RAISE;
END;
