sqlMap.delete:
BEGIN
DELETE FROM PD_SUJUMD WHERE 1=1 AND PREE_CODE = #PREE_CODE# AND PREE_IDXX = #PREE_IDXX# ;
DELETE FROM PD_ESTIMD WHERE 1=1 AND PREE_CODE = #PREE_CODE# AND PREE_IDXX = #PREE_IDXX# ;
END;



		BEGIN

	     	DELETE FROM PD_ESTIMH
			WHERE 1=1
			AND JOBN_NUMB = #JOBN_NUMB#
        	;

	     	DELETE FROM PD_ESTIMD
			WHERE 1=1
			AND JOBN_NUMB = #JOBN_NUMB#
        	;

        END;

sqlMap.insert:
BEGIN
INSERT INTO PD_SUJUMH ( /* PDMC0021.SAVE00.제작 견적서 */ PREE_CODE /* 견적번호 */ ,PREE_NAME /* 견적명 */ ,ESTI_DATE /* 견적일자 */ ,JOBN_NUMB /* 제작번호 */ ,ESTI_AMTX /* 견적금액 */ ,DEND_AMTX /* 청구금액 */ ,MEMO_FILD /* 비고 */ ,INST_USID /* 생성자 */ ,INST_DATE /* 생성일자 */ ,UPDT_USID /* 수정자 */ ,UPDT_DATE /* 수정일자 */ ) VALUES ( #PREE_CODE# /* 견적번호 */ ,#PREE_NAME# /* 견적명 */ ,#ESTI_DATE# /* 견적일자 */ ,#JOBN_NUMB# /* 제작번호 */ ,#ESTI_AMTX# /* 견적금액 */ ,#DEND_AMTX# /* 청구금액 */ ,#MEMO_FILD# /* 비고 */ ,#UPDT_USID# /*입력작업자*/ ,TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*입력일시*/ ,#UPDT_USID# /*수정작업자*/ ,TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*수정일시*/ );
INSERT INTO PD_ESTIMH ( /* PDMC0021.SAVE00.제작 견적서 -견적용 헤더 *//* [제작견적서 견적용 헤더] */ PREE_CODE /* 견적번호 */ ,PREE_NAME /* 견적명 */ ,ESTI_DATE /* 견적일자 */ ,JOBN_NUMB /* 제작번호 */ ,ESTI_AMTX /* 견적금액 */ ,MEMO_FILD /* 비고 */ ,INST_USID /* 생성자 */ ,INST_DATE /* 생성일자 */ ,UPDT_USID /* 수정자 */ ,UPDT_DATE /* 수정일자 */ ) VALUES ( #PREE_CODE# /* 견적번호 */ ,#PREE_NAME# /* 견적명 */ ,#ESTI_DATE# /* 견적일자 */ ,#JOBN_NUMB# /* 제작번호 */ ,#ESTI_AMTX# /* 견적금액 */ ,#MEMO_FILD# /* 비고 */ ,#UPDT_USID# /*입력작업자*/ ,TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*입력일시*/ ,#UPDT_USID# /*수정작업자*/ ,TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*수정일시*/ );
END;

BEGIN
INSERT INTO PD_SUJUMD ( /* PDMC0021.SAVE01.제작 견적서 하단 *//* [제작정산 하단 상세 저장] */ PREE_CODE /* 견적번호 */ ,PREE_NAME /* 견적명 */ ,PREE_IDXX /* 견적순번 */ ,ESTI_DATE /* 견적일자 */ ,JOBN_NUMB /* 제작번호 */ ,ITEM_CODE /* 제작항목 */ ,TYPE_QTYX /* 수량 */ ,ESTI_PRCE /* 견적단가 */ ,ESTI_AMTX /* 견적금액 */ ,DEND_AMTX /* 청구금액 */ ,SUJU_AMTX /* 정산금액 */ ,OUTS_CODE /* 제작사코드 */ ,CONT_NUMB /* 계약서번호 */ ,MEMO_FILD /* 비고내역 */ ,SORT_ORDR /* 정렬구분 */ ,SUJU_DATE /* 정산일자 */ ,INST_USID /* 생성자 */ ,INST_DATE /* 생성일자 */ ,UPDT_USID /* 수정자 */ ,UPDT_DATE /* 수정일자 */ ) VALUES ( #PREE_CODE# /* 견적번호 */ ,#PREE_NAME# /* 견적명 */ ,#PREE_IDXX# /* 견적순번 */ ,#ESTI_DATE# /* 견적일자 */ ,#JOBN_NUMB# /* 제작번호 */ ,#ITEM_CODE# /* 제작항목 */ ,#TYPE_QTYX# /* 수량 */ ,#ESTI_PRCE# /* 견적단가 */ ,#ESTI_AMTX# /* 견적금액 */ ,#DEND_AMTX# /* 청구금액 */ ,#SUJU_AMTX# /* 정산금액 */ ,#OUTS_CODE# /* 제작사코드 */ ,#CONT_NUMB# /* 계약서번호 */ ,#MEMO_FILD# /* 비고내역 */ ,#SORT_ORDR# /* 정렬구분 */ ,#SUJU_DATE# /* 정산일자 */ ,#UPDT_USID# /*입력작업자*/ ,TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*입력일시*/ ,#UPDT_USID# /*수정작업자*/ ,TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*수정일시*/ );
