sqlMap.update:
BEGIN
UPDATE PD_SUJUMD /* PDMC0021.UPDATE00.제작 견적서 */ SET ESTI_DATE = #ESTI_DATE# /* 견적일자 */ , PREE_NAME = #PREE_NAME# /* 견적명 */ , JOBN_NUMB = #JOBN_NUMB# /* 제작번호 */ , ITEM_CODE = #ITEM_CODE# /* 제작항목코드 */ , TYPE_QTYX = #TYPE_QTYX# /* 수량 */ , ESTI_PRCE = #ESTI_PRCE# /* 견적단가 */ , ESTI_AMTX = #ESTI_AMTX# /* 견적금액 */ , DEND_AMTX = #DEND_AMTX# /* 청구금액 */ , OUTS_CODE = #OUTS_CODE# /* 제작사코드 */ , MEMO_FILD = #MEMO_FILD# /* 비고 */ , SORT_ORDR = #SORT_ORDR# /* 정렬구분 */ , REF1_FILD = #REF1_FILD# /* 비고1 */ , REF2_FILD = #REF2_FILD# /* 비고2 */ , REF3_FILD = #REF3_FILD# /* 비고3 */ , REF4_FILD = #REF4_FILD# /* 비고4 */ , UPDT_USID = #UPDT_USID# /*수정자*/ ,UPDT_DATE = TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*입력일시*/ WHERE 1=1 AND PREE_CODE = #PREE_CODE# /* 견적번호 */ AND PREE_IDXX = #PREE_IDXX# /* 견적순번 */
;
UPDATE PD_ESTIMD /* PDMC0010.UPDATE01.제작 견적서 -견적용 상세 *//* [제작견적서 견적용 상세] */ SET ESTI_DATE = #ESTI_DATE# /* 견적일자 */ , PREE_NAME = #PREE_NAME# /* 견적명 */ , JOBN_NUMB = #JOBN_NUMB# /* 제작번호 */ , ITEM_CODE = #ITEM_CODE# /* 제작항목코드 */ , TYPE_QTYX = #TYPE_QTYX# /* 수량 */ , ESTI_PRCE = #ESTI_PRCE# /* 견적단가 */ , ESTI_AMTX = #ESTI_AMTX# /* 견적금액 */ , OUTS_CODE = #OUTS_CODE# /* 제작사코드 */ , MEMO_FILD = #MEMO_FILD# /* 비고 */ , SORT_ORDR = #SORT_ORDR# /* 정렬구분 */ , UPDT_USID = #UPDT_USID# /*수정자*/ ,UPDT_DATE = TO_CHAR(SYSDATE, 'YYYY-MM-DD AM HH:MI:SS') /*입력일시*/ WHERE 1=1 AND PREE_CODE = #PREE_CODE# /* 견적번호 */ AND PREE_IDXX = #PREE_IDXX# /* 견적순번 */
;
END;

BEGIN
UPDATE PD_SUJUMH SET SUJU_AMTX = ( SELECT NVL(SUM(SUJU_AMTX),0) FROM PD_SUJUMD WHERE 1=1 AND PREE_CODE = #PREE_CODE# ) , ESTI_AMTX = ( SELECT NVL(SUM(ESTI_AMTX),0) FROM PD_SUJUMD WHERE 1=1 AND PREE_CODE = #PREE_CODE# ) , DEND_AMTX = ( SELECT NVL(SUM(DEND_AMTX),0) FROM PD_SUJUMD WHERE 1=1 AND PREE_CODE = #PREE_CODE# ), ESTI_DATE = #ESTI_DATE# WHERE 1=1 AND PREE_CODE = #PREE_CODE# ;
UPDATE PD_ESTIMH SET ESTI_AMTX = ( SELECT SUM(ESTI_AMTX) FROM PD_ESTIMD WHERE 1=1 AND PREE_CODE = #PREE_CODE# ), ESTI_DATE = #ESTI_DATE# WHERE PREE_CODE = #PREE_CODE# ;
END;
