sqlMap.select:
SELECT /* PRPA0110.SEARCH00.실적집계표[모델료집계] */ #CUTY_CODE# CUTY_CODE ,#YEAR_MONT1# YEAR_MONT1 ,#YEAR_MONT2# YEAR_MONT2 , OUTS_CODE /* 제작사코드 */ , CUST_NAME /* 제작사 */ , JOBN_NUMB /* 제작번호 */ , JOBN_NAME /* 제작건명 */ , SUM(DEND_AMTX) AS SUJU_AMTX /* 청구금액 */ , DEND_DATE /* 청구일자 */ FROM ( SELECT 1 AS SORT_GUBN , A.OUTS_CODE , TO_CHAR(D.CUST_NAME) AS CUST_NAME , A.JOBN_NUMB , B.JOBN_NAME , A.DEND_AMTX AS DEND_AMTX , B.DEND_DATE , G.ROW_NUM FROM PD_SUJUMD A LEFT JOIN PD_JOBNXM B ON A.JOBN_NUMB = B.JOBN_NUMB /* LEFT JOIN PD_CLASXM C ON A.OUTS_CODE = C.CUST_CODE */ LEFT JOIN TA_CUSTXM D ON A.OUTS_CODE = D.CUST_CODE LEFT JOIN PD_ITEMXD E ON A.ITEM_CODE = E.ITEM_CODE LEFT JOIN TA_CUSTXM F ON B.CUST_CODE = F.CUST_CODE LEFT JOIN ( SELECT A.OUTS_CODE , SUM(A.DEND_AMTX) AS DEND_AMTX , ROW_NUMBER() OVER (ORDER BY SUM(A.DEND_AMTX) DESC) AS ROW_NUM FROM PD_SUJUMD A LEFT JOIN PD_JOBNXM B ON A.JOBN_NUMB = B.JOBN_NUMB /* LEFT JOIN PD_CLASXM C ON A.OUTS_CODE = C.CUST_CODE */ LEFT JOIN TA_CUSTXM D ON A.OUTS_CODE = D.CUST_CODE LEFT JOIN PD_ITEMXD E ON A.ITEM_CODE = E.ITEM_CODE LEFT JOIN TA_CUSTXM F ON B.CUST_CODE = F.CUST_CODE WHERE 1=1 /* AND NVL(C.AGCY_YSNO,'N') = 'Y' */ AND NVL(E.MODL_FLAG,'N') = 'Y'
GROUP BY A.OUTS_CODE )G ON A.OUTS_CODE = G.OUTS_CODE WHERE 1=1 /* AND NVL(C.AGCY_YSNO,'N') = 'Y' */ AND NVL(E.MODL_FLAG,'N') = 'Y'
)A WHERE 1=1 GROUP BY SORT_GUBN, OUTS_CODE , CUST_NAME , JOBN_NUMB , JOBN_NAME , DEND_DATE , ROW_NUM ORDER BY ROW_NUM, JOBN_NUMB, SORT_GUBN
