sqlMap.insert:
DECLARE /* TMMA0060.SAVE_NOWX_VALU.현재 일련번호 조정 */ v_ROWCNT INT;
BEGIN SELECT COUNT(1) INTO v_ROWCNT FROM TM_SEQDTL WHERE TABLE_NAME = #TABLE_NAME# /* 테이블명 */ AND COLUMN_NAME = #COLUMN_NAME# /* 컬럼명 */ AND SECH_COND = #SECH_COND# /* 조회조건명 */;
IF v_ROWCNT < 1 THEN INSERT INTO TM_SEQDTL ( TABLE_NAME /* 테이블명 */ , COLUMN_NAME /* 컬럼명 */ , SECH_COND /* 조회조건명 */ , NOWX_VALU /* 현재값 */ ) VALUES ( #TABLE_NAME# /* 테이블명 */ , #COLUMN_NAME# /* 컬럼명 */ , #SECH_COND# /* 조회조건명 */ , #NOWX_VALU# /* 현재값 */ );
ELSE
UPDATE TM_SEQDTL SET NOWX_VALU = #NOWX_VALU# /* 현재값 */ WHERE TABLE_NAME = #TABLE_NAME# /* 테이블명 */ AND COLUMN_NAME = #COLUMN_NAME# /* 컬럼명 */ AND SECH_COND = #SECH_COND# /* 조회조건명 */ ;
END IF; END;
