sqlMap.update:
DECLARE /* YEAA0060.PROC00.개인자료등록확인 복사 */ /*전녀도 자료 존재 여부*/ vYEA_YETABS_CNT INT; BEGIN SELECT COUNT(*) INTO vYEA_YETABS_CNT FROM YEA_YETABS WHERE YEA_STD_YY = #SRC_YEA_STD_YY#;
IF vYEA_YETABS_CNT = 0 THEN RAISE_APPLICATION_ERROR(-20000, SF_GET_MESSAGE('YEA004')); END IF;
/*산출근거삭제*/ DELETE FROM YEA_YETABS WHERE YEA_STD_YY = #TAR_YEA_STD_YY# ;
INSERT INTO YEA_YETABS ( YEA_STD_YY /* YEA_기준년도 */ ,YEA_TAX_STD_SEQ /* YEA_세액기초일련번호 */ ,YEA_DED_TYPE_CD /* YEA_공제구분코드 */ ,YEA_DED_ITEM_CD /* YEA_공제항목코드 */ ,YEA_DED_ITEM_NM /* YEA_공제항목명 */ ,YEA_DED_CONTENT /* YEA_공제내용 */ ,YEA_A_VALUE /* YEA_A값 */ ,YEA_B_VALUE /* YEA_B값 */ ,YEA_C_VALUE /* YEA_C값 */ ,YEA_D_VALUE /* YEA_D값 */ ,YEA_E_VALUE /* YEA_E값 */ ,YEA_NOTE /* YEA_비고 */ ,YEA_SORT_ORD /* YEA_정렬순서 */ ,YEA_INS_USER_ID /* YEA_입력작업자 */ ,YEA_INS_DATE /* YEA_입력일시 */ ,YEA_UPD_USER_ID /* YEA_수정작업자 */ ,YEA_UPD_DATE /* YEA_수정일시 */ ) SELECT #TAR_YEA_STD_YY# /* YEA_기준년월*/ ,A.YEA_TAX_STD_SEQ /* YEA_세액기초일련번호 */ ,A.YEA_DED_TYPE_CD /* YEA_공제구분코드 */ ,A.YEA_DED_ITEM_CD /* YEA_공제항목코드 */ ,A.YEA_DED_ITEM_NM /* YEA_공제항목명 */ ,A.YEA_DED_CONTENT /* YEA_공제내용 */ ,A.YEA_A_VALUE /* YEA_A값 */ ,A.YEA_B_VALUE /* YEA_B값 */ ,A.YEA_C_VALUE /* YEA_C값 */ ,A.YEA_D_VALUE /* YEA_D값 */ ,A.YEA_E_VALUE /* YEA_E값 */ ,A.YEA_NOTE /* YEA_비고 */ ,A.YEA_SORT_ORD /* YEA_정렬순서 */ ,#UPDT_USID# /* YEA_입력작업자 */ ,SYSDATE /* YEA_입력일시 */ ,#UPDT_USID# /* YEA_수정작업자 */ ,SYSDATE /* YEA_수정일시 */ FROM YEA_YETABS A /* [YEA_연말정산세액계산기초] */ WHERE A.YEA_STD_YY = #SRC_YEA_STD_YY#;
END;
